import React, { useState } from "react";

const Dashboard = () => {
  const [studentId, setStudentId] = useState("");
  const [studentName, setStudentName] = useState("");
  const [grade, setGrade] = useState("");
  // const [subjects, setSubjects] = useState({ name: "", testId: "" });
  const [subjects, setSubjects] = useState([]);
  const [sections, setSections] = useState({
    standrd1: { pointsEarned: "", pointsPossible: "" },
    standrd2: { pointsEarned: "", pointsPossible: "" },
    standrd3: { pointsEarned: "", pointsPossible: "" },
    standrd4: { pointsEarned: "", pointsPossible: "" },
    standrd5: { pointsEarned: "", pointsPossible: "" },
    standrd6: { pointsEarned: "", pointsPossible: "" }
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const validatePoints = (pointsEarned, pointsPossible) => {
    const min = 0;
    const max = 100;
    const errors = {};

    if (isNaN(pointsEarned) || pointsEarned < min || pointsEarned > max) {
      errors.pointsEarned = `Points earned must be a number between ${min} and ${max}.`;
    }

    if (isNaN(pointsPossible) || pointsPossible < min || pointsPossible > max) {
      errors.pointsPossible = `Points possible must be a number between ${min} and ${max}.`;
    }

    return errors;
  };

  const handleChange = (section, field, value) => {
    setSections((prev) => {
      const updatedSections = {
        ...prev,
        [section]: { ...prev[section], [field]: value }
      };

      // Validate the input
      const sectionErrors = validatePoints(
        updatedSections[section].pointsEarned,
        updatedSections[section].pointsPossible
      );

      setErrors((prevErrors) => ({
        ...prevErrors,
        [`${section}pointsEarned`]: sectionErrors.pointsEarned,
        [`${section}pointsPossible`]: sectionErrors.pointsPossible
      }));

      return updatedSections;
    });
  };

  const addSubject = () => {
    setSubjects((prevSubjects) => [
      ...prevSubjects,
      { name: "", testId: "", sections: { ...sections } }
    ]);
  };

  const handleSubjectChange = (index, field, value) => {
    setSubjects((prev) => {
      const updatedSubjects = [...prev];
      updatedSubjects[index][field] = value;
      return updatedSubjects;
    });
  };
  const handleSubmit = async () => {
    setIsSubmitting(true);

    let hasErrors = false;

    // Validate sections for all subjects
    subjects.forEach((subject, index) => {
      Object.keys(subject.sections).forEach((section) => {
        const { pointsEarned, pointsPossible } = subject.sections[section];
        const sectionErrors = validatePoints(pointsEarned, pointsPossible);

        if (Object.keys(sectionErrors).length > 0) {
          hasErrors = true;
          setErrors((prevErrors) => ({
            ...prevErrors,
            [`subject${index}${section}pointsEarned`]:
              sectionErrors.pointsEarned,
            [`subject${index}${section}pointsPossible`]:
              sectionErrors.pointsPossible
          }));
        }
      });
    });

    if (hasErrors) {
      setIsSubmitting(false);
      return;
    }

    // Calculate totals for each subject
    const subjectsWithTotals = subjects.map((subj) => {
      const totals = Object.values(subj.sections).reduce(
        (acc, section) => {
          acc.pointsEarned += Number(section.pointsEarned || 0);
          acc.pointsPossible += Number(section.pointsPossible || 0);
          return acc;
        },
        { pointsEarned: 0, pointsPossible: 0 }
      );
      return {
        ...subj,
        totalPointsEarned: totals.pointsEarned,
        totalPointsPossible: totals.pointsPossible
      };
    });

    if (hasErrors) {
      setIsSubmitting(false);
      return;
    }

    // Prepare the payload
    // const payload = {
    //   studentId,
    //   testSubjectData: [
    //     {
    //       Name: subject.name,
    //       testId: subject.testId,
    //       sections
    //     }
    //   ]
    // };
    const payload = {
      studentId,
      studentName, //Incldes student name
      grade, // Include student grade
      testSubjectData: [subjectsWithTotals]
    };

    try {
      const response = await fetch(
        "http://localhost:3000/student/updateTestSubjects",
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload)
        }
      );

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || "Failed to update the document.");
      }

      console.log("Update successful:", result);
      alert("Subjects updated successfully!");
    } catch (err) {
      console.error("Error during submission:", err);
      alert(`Error: ${err.message}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <h1>Enter Section Points</h1>
      {/* Inputs for Student ID and Subject Details */}
      <div>
        <label>
          Student ID:
          <input
            type="text"
            placeholder="Enter Student ID"
            value={studentId}
            onChange={(e) => setStudentId(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Student Name:
          <input
            type="text"
            placeholder="Enter Student Name"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Grade:
          <input
            type="text"
            placeholder="Enter Grade"
            value={grade}
            onChange={(e) => setGrade(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Subject Name:
          <input
            type="text"
            placeholder="Enter Subject Name"
            value={subject.name}
            onChange={(e) =>
              setSubject((prev) => ({ ...prev, name: e.target.value }))
            }
          />
        </label>
      </div>
      <div>
        <label>
          Test ID:
          <input
            type="text"
            placeholder="Enter Test ID"
            value={subject.testId}
            onChange={(e) =>
              setSubject((prev) => ({ ...prev, testId: e.target.value }))
            }
          />
        </label>
      </div>

      {/* Inputs for Section Points */}
      {Object.keys(sections).map((section) => (
        <div key={section}>
          <h3>{section}</h3>
          <input
            type="number"
            placeholder="Points Earned"
            value={sections[section].pointsEarned}
            onChange={(e) =>
              handleChange(section, "pointsEarned", Number(e.target.value))
            }
          />
          {errors[`${section}pointsEarned`] && (
            <p style={{ color: "red" }}>{errors[`${section}pointsEarned`]}</p>
          )}
          <input
            type="number"
            placeholder="Points Possible"
            value={sections[section].pointsPossible}
            onChange={(e) =>
              handleChange(section, "pointsPossible", Number(e.target.value))
            }
          />
          {errors[`${section}pointsPossible`] && (
            <p style={{ color: "red" }}>{errors[`${section}pointsPossible`]}</p>
          )}
        </div>
      ))}

      {/* Submit Button */}
      <button onClick={handleSubmit} disabled={isSubmitting}>
        {isSubmitting ? "Submitting..." : "Submit"}
      </button>
    </div>
  );
};

